export class ZooClient {
  private baseURL: string;

  constructor(config: { baseURL: string }) {
    this.baseURL = config.baseURL;
  }

  async getAnimals() {
    const response = await fetch(`${this.baseURL}/animals`);
    return response.json();
  }
}

